/** ADVENT 2020 - DAY 06 - PART I **/

CREATE TABLE #CUSTOM_TEMP
(ROW_ID INT, GROUP_ID INT, GROUP_TXT NVARCHAR(1))
GO

SELECT * INTO #TEMP  FROM ADVENT_DEC_06_CUSTOM 

DECLARE @group_cnt int;
DECLARE @letter_cnt int;
DECLARE @cust_id int;
DECLARE @cust_txt_len int;
DECLARE @cust_txt nvarchar(26);
DECLARE @insert_txt nvarchar(1);

set @group_cnt = 1;
set @letter_cnt = 1;

while exists (select * from #TEMP)
	BEGIN	SELECT TOP 1 @cust_id = ID_NUM, @cust_txt = CUSTOM_TXT, @cust_txt_len = LEN(CUSTOM_TXT)
			FROM #TEMP

			set @letter_cnt = 1

			IF @cust_txt = ''
				BEGIN
					SET @group_cnt = @group_cnt + 1
				END
			
			while @cust_txt_len >= @letter_cnt
				BEGIN	set @insert_txt = SUBSTRING(@cust_txt, @letter_cnt, 1)
						print @insert_txt
						set @letter_cnt = @letter_cnt + 1
						INSERT INTO #CUSTOM_TEMP (ROW_ID, GROUP_ID, GROUP_TXT)
						VALUES (@cust_id, @group_cnt, @insert_txt)
				END

			DELETE FROM #TEMP WHERE ID_NUM = @cust_id
	END
GO

SELECT SUM(GROUP_ROLL.GROUP_CNT) AS PART_I_ANSWER
FROM
	(SELECT DISTINCT_LETTER.GROUP_ID, COUNT(DISTINCT_LETTER.GROUP_TXT) AS GROUP_CNT
	FROM
		(SELECT DISTINCT GROUP_ID, GROUP_TXT FROM #CUSTOM_TEMP) AS DISTINCT_LETTER
	GROUP BY DISTINCT_LETTER.GROUP_ID ) AS GROUP_ROLL
GO

/** ADVENT 2020 - DAY 06 - PART II **/

	SELECT COUNT(*) PART_II_ANSWER
	FROM (	SELECT #CUSTOM_TEMP.GROUP_ID, #CUSTOM_TEMP.GROUP_TXT, COUNT(#CUSTOM_TEMP.ROW_ID) AS ANSWER_QTY, GET_GROUP_QTY.GROUP_QTY
			FROM #CUSTOM_TEMP
				JOIN (	SELECT GROUP_ID, COUNT ( DISTINCT ROW_ID) AS GROUP_QTY
						FROM #CUSTOM_TEMP
						GROUP BY GROUP_ID) AS GET_GROUP_QTY ON #CUSTOM_TEMP.GROUP_ID = GET_GROUP_QTY.GROUP_ID
				GROUP BY #CUSTOM_TEMP.GROUP_ID, #CUSTOM_TEMP.GROUP_TXT, GET_GROUP_QTY.GROUP_QTY
				HAVING  COUNT(#CUSTOM_TEMP.ROW_ID) = GET_GROUP_QTY.GROUP_QTY ) AS TEMP
GO

